mapper.update:
update contract
set material_id = #{materialId,jdbcType=VARCHAR},
  contract_file_pic_url = #{contractFilePicUrl,jdbcType=VARCHAR},
  contract_status = #{contractStatus,jdbcType=VARCHAR}
where contract_id = #{contractId,jdbcType=VARCHAR}

mapper.select:
select contract_id, material_id, contract_file_pic_url, contract_status
from contract
where contract_id = #{contractId,jdbcType=VARCHAR}
